from math import pi


def findSurfaceAreaOfACone(radius: float, slantHeight: float) -> None:
    """
    find surface Area of A Cone
    Arguments:
        radius: float
        slantHeight: float
    Returns:
        SurfaceArea: float
    Surface area of cone = π * radius * (radius + slantHeight)
    """

